import React, { useEffect, useState } from "react";
import SearchForm from "./components/Search";

import { useLazyAxios } from "use-axios-client";

type SearchResult = {
  id: string;
  title: string;
  url: string;
  description: string;
  category: "VIDEOS" | "PLAYLISTS" | "BLOG_POSTS";
};

const baseURL = "/api/data?search=";

function App() {
  const [searchValue, setSearchValue] = useState<string>("");
  const [resultValue, setResultValue] = useState<SearchResult[] | null>(null);

  const [getData, { data, error, loading }] = useLazyAxios({
    url: `https://jsonplaceholder.typicode.com/posts/${searchValue}`,
  });

  if (loading || !data) return "Loading...";
  if (error) return "Error!";

  return (
    <div className="App">
      <header className="App-header">
        <h1>Search UI Challenge</h1>

        {error && <div>{error}</div>}

        <SearchForm handleSearch={(search) => getData({ search })} />
      </header>
    </div>
  );
}

export default App;
