import React, { useEffect, useState } from "react";
import SearchForm from "./components/Search";
import axios from "axios";
import { Box, CircularProgress } from "@mui/material";

type SearchResult = {
  id: string;
  title: string;
  url: string;
  description: string;
  category: "VIDEOS" | "PLAYLISTS" | "BLOG_POSTS";
};

const baseURL = "/api/data?search=";

function App() {
  const [error, setError] = useState(null);
  const [searchValue, setSearchValue] = useState<string | null>(null);
  const [resultValue, setResultValue] = useState<SearchResult[] | null>(null);
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    if (searchValue) {
      setIsLoading(true);
      setResultValue(null);
      axios
        .get(`${baseURL}${searchValue}`)
        .then((response) => {
          setResultValue(response.data);
        })
        .catch((error) => {
          setError(error);
          console.log(error.config);
        })
        .finally(() => setIsLoading(false));
    }
  }, [searchValue]);

  console.log(isLoading);
  return (
    <div className="App">
      <header className="App-header">
        <h1>Search UI Challenge</h1>
        <SearchForm handleSearch={setSearchValue} />
      </header>

      <Box sx={{ my: 2 }}>
        {isLoading && <CircularProgress />}

        {resultValue &&
          (resultValue.length > 0
            ? resultValue.map((item) => (
                <Box sx={{ my: 2, p: 2, border: "1px solid black" }}>
                  <h2>{item.title}</h2>
                  <h3>{item.description}</h3>
                </Box>
              ))
            : "No results found. Try another search")}
      </Box>
    </div>
  );
}

export default App;
