import React, { useEffect, useState } from "react";
import SearchForm from "./components/Search";

type SearchResult = {
  id: string;
  title: string;
  url: string;
  description: string;
  category: "VIDEOS" | "PLAYLISTS" | "BLOG_POSTS";
};

function App() {
  const [loading, setLoading] = useState(false);
  const limit = 10;
  const searchQuery = "pepe";

  const fetchGetUserApi = async (api: RequestInfo | URL) => {
    setLoading(true);
    const response = await fetch(api, { mode: "cors" });
    console.log(response);

    //const json = await response.json();
    setLoading(false);

    //console.log(json);
  };

  useEffect(() => {
    (async () => {
      let api = `http://localhost:3001/api/data?search=${searchQuery}`;
      await fetchGetUserApi(api);
    })();
  }, [searchQuery]);

  return (
    <div className="App">
      <header className="App-header">
        <h1>Search UI Challenge</h1>
        <SearchForm />
      </header>
    </div>
  );
}

export default App;
