import React, { useEffect, useState } from "react";
import SearchForm from "./components/Search";
import axios from "axios";
import { Box } from "@mui/material";

type SearchResult = {
  id: string;
  title: string;
  url: string;
  description: string;
  category: "VIDEOS" | "PLAYLISTS" | "BLOG_POSTS";
};

const baseURL = "/api/data?search=";

function App() {
  const [error, setError] = useState(null);
  const [searchValue, setSearchValue] = useState<string | null>(null);
  const [resultValue, setResultValue] = useState<SearchResult[] | null>(null);
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    setIsLoading(true);

    searchValue &&
      axios
        .get(`${baseURL}${searchValue}`)
        .then((response) => {
          setResultValue(response.data);
        })
        .catch((error) => {
          setError(error);
        })
        .finally(() => setIsLoading(false)); // complete loading success/fail
  }, [searchValue]);

  console.log(error.config);

  return (
    <div className="App">
      <header className="App-header">
        <h1>Search UI Challenge</h1>
        <SearchForm handleSearch={setSearchValue} />
      </header>

      {resultValue &&
        resultValue.map((item) => (
          <Box sx={{ my: 2, p: 2, border: "1px solid black" }}>
            <h2>{item.description}</h2>
            <h3>{item.description}</h3>
          </Box>
        ))}
    </div>
  );
}

export default App;
