import React, { useEffect, useState } from "react";
import SearchForm from "./components/Search";
import axios from "axios";
import { Box, CircularProgress } from "@mui/material";

type SearchResult = {
  id: string;
  title: string;
  url: string;
  description: string;
  category: "VIDEOS" | "PLAYLISTS" | "BLOG_POSTS";
};

const baseURL = "/api/data?search=";
const categories = {
  VIDEOS: "Video",
  PLAYLISTS: "Playlist",
  BLOG_POSTS: "Blog Post",
};

function App() {
  const [error, setError] = useState<Error | null>(null);
  const [searchValue, setSearchValue] = useState<string | null>(null);
  const [resultValue, setResultValue] = useState<SearchResult[] | null>(null);
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    if (searchValue) {
      setIsLoading(true);
      setResultValue(null);
      axios
        .get(`${baseURL}${searchValue}`)
        .then((response) => {
          setResultValue(response.data);
        })
        .catch((error) => {
          setError(error);
          console.log(error.config);
        })
        .finally(() => setIsLoading(false));
    }
  }, [searchValue]);

  return !error ? (
    <div className="App">
      <header className="App-header">
        <h1>Search UI Challenge</h1>
        <SearchForm handleSearch={setSearchValue} />
      </header>

      <Box sx={{ my: 2 }}>
        {isLoading && <CircularProgress />}

        {resultValue &&
          (resultValue.length > 0
            ? resultValue.map((item) => (
                <Box sx={{ my: 2, p: 2, border: "1px solid black" }}>
                  {categories[item.category]}
                  <h2>
                    <a href={item.url} target="_blank" rel="noreferrer">
                      {item.title}
                    </a>
                  </h2>
                  <h3>{item.description}</h3>
                </Box>
              ))
            : "There are no results matching your query.")}
      </Box>
    </div>
  ) : (
    <>
      <h1>Error</h1>
      {error.message}
    </>
  );
}

export default App;
